<?php

/**
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class IgsTable extends DarwinTable
{
  public function getIgLike($ig_num, $from_date, $orderBy='ig_num', $orderByOrder='asc')
  {
    $q = Doctrine_Query::create()
         ->from('Igs i');
    if (trim($ig_num) != ""):
      $q->andWhere("i.ig_num like ? ", "$ig_num%");
    endif;
    if ($from_date->getMask() > 0):
      $q->andWhere(" i.ig_date >= ? ", $from_date->format('d/m/Y'));
    endif;
    $q->andWhere("i.id > 0 ")
      ->orderby($orderBy . ' ' . $orderByOrder);
    return $q;
  }
}