class PartSearch extends BasePartSearch
{
  public function getAggregatedName($sep = ' / ')
  {
    $items = array(
      $this->getTaxonName(),
      $this->getChronoName(),
      $this->getLithoName(),
      $this->getLithologyName(),
      $this->getMineralName()
    );
    $items = array_filter($items);
    return implode($sep, $items);
  }
}
